from pyteal import *

def approval_program():
    is_update_application = Txn.on_completion() == OnComplete.UpdateApplication

    program = Seq([
        Cond(
            [is_update_application, Seq([
                Log(Bytes("Update Application Detected")),
                Approve()
            ])]
        ),

        Log(Bytes("Contratto approvato")),

        Return(Int(1))
    ])
    return program

def clear_program():
    program = Seq([
        Log(Bytes("Clear eseguito")),
        Return(Int(1))
    ])
    return program

if __name__ == '__main__':
    print(compileTeal(approval_program(), mode=Mode.Application, version=5))
